import Augmentorimport osNUM_AUGMENTED_IMAGES = 2534# Defining train images locationproject_base_dir = os.path.dirname(os.getcwd())path_to_train_data = os.path.join(project_base_dir, "data/train/NORMAL")# Augment training datap = Augmentor.Pipeline(path_to_train_data)p.rotate(1, 10, 10)p.zoom(1, 1, 1.2)p.flip_left_right(0.5)p.sample(NUM_AUGMENTED_IMAGES)# Move images to train folder and delete output foldercommand = f"mv {path_to_train_data}/output/*.png {path_to_train_data}"os.system(command)os.system(f"rm -r {path_to_train_data}/output")